mapper.select:

	SELECT *
	FROM	(SELECT /*+INDEX_DESC(mvc_board pk_board) */
				rownum rn, bId, bName, bTitle, bDate, bHit,bFileName 
			from mvc_board where rownum <= #{pageNum} * #{amount})
	where rn > (#{pageNum}-1) * #{amount}
	ORDER BY bId DESC

mapper.update:

	UPDATE mvc_board SET
	bHit = bHit+1 
	WHERE bId = #{bId}


UPDATE mvc_board SET
bTitle = #{bTitle},
bContent = #{bContent}
WHERE bId = #{bId}
